import React from "react";
import Cell from "./Cells/Cell";
import styles from "./Table.module.css"

function createCell(i,j,size){
	return {i:i,j:j,size:size};
}
function createTable(n){
	const table=[];
	const size = n*n;
	for(let i=0;i<n;i++){
		for(let j =0;j<n;j++){
			table.push(createCell(i,j,size));
		}
	}
	return table;
}

function Table(){
	const n =8;
	const tableStyle = {width: "50%",height: "0%"};
	const board = createTable(n);
	return (
		<div style={tableStyle}>
			{board.map(cell => <Cell size={cell.size}/>)}
		</div>
	)
}


export default Table;
