import React, { useState } from "react";
import Cell from "./Cells/Cell";
import styles from "./Board.module.css"
import {createBoard} from "./boardUtils"



function Board(){
	function handleClick(target){

		if(selected === null){
			selected = target;
		}else{
			setBoard(prev =>{
				const newBoard = prev;
				const selectedCell = selected.i+selected.j*prev.n;
				const targetCell = target.i+target.j*prev.n;
				newBoard.cells[targetCell].piece = prev[selectedCell].piece;
				newBoard.cells[selectedCell].piece = null;
				selected = null;
				return newBoard
			})
		}
	}
	const n =8;
	const [board,setBoard] = useState(createBoard(n));
	let selected = null;
	return (
		<div className={styles.board}>
			{board.cells.map((cell,index) =>
			<Cell 
			key ={index}
			n={board.n} 
			i={cell.i} 
			j={cell.j} 
			piece={cell.piece}
			onSelected={handleClick}
			/>)}
		</div>
	)
}


export default Board;
