import React, { useState } from "react";
import Cell from "./Cells/Cell";
import styles from "./Board.module.css"
import {createBoard} from "./boardUtils"



function Board(){
	function handleClick(event){
		const target = event.target;
		if(selected === null){
			setSelected({i:target.i,j:target.j});
		}else{
			setBoard(prev =>{
				const newBoard = prev;
				const selectedCell = selected.i+selected.j*prev.n;
				const targetCell = target.i+target.j*prev.n;
				newBoard[targetCell].piece = prev[selectedCell].piece;
				newBoard[selectedCell].piece = null;
				console.log(newBoard);
				return newBoard
			})
		}
	}
	const n =8;
	const [board,setBoard] = useState(createBoard(n));
	const [selected,setSelected] = useState(null);

	return (
		<div className={styles.board}>
			{board.cells.map((cell,index) =>
			<Cell 
			key ={index}
			n={board.n} 
			i={cell.i} 
			j={cell.j} 
			piece={cell.piece}
			/>)}
		</div>
	)
}


export default Board;
