import React from "react";
import Cell from "./Cells/Cell";
import styles from "./Table.module.css"

function createCell(n,i,j){
	return {n:n,i:i,j:j};
}
function createTable(n){
	const table=[];
	for(let i=0;i<n;i++){
		for(let j =0;j<n;j++){
			table.push(createCell(n,i,j));
		}
	}
	return table;
}

function Table(){
	const n =8;
	const tableStyle = {width: "50%",height: "60%"};
	const board = createTable(n);
	return (
		<div 
		className={styles.table}
		style={tableStyle}>
			{board.map(cell =>
			 (<Cell
			 n={cell.n}
			 i={cell.i}
			 j={cell.j}
			 />))}
		</div>
	)
}


export default Table;
